import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent { }
<div class="about-container">
    <h2>About This Application</h2>
    <p>
        This is a simple inventory management system that allows users to view, add, edit, and delete products.
        It uses **Angular** for the front-end and a **JSON server** for the back-end.
    </p>
</div>
